mapper.insert:
INSERT INTO CATEGORY
(
	ID
	,CATEGORY_NAME
	,`DESC`
	,PARENT_CATEGORY_ID
	,IMAGE_URL
)
VALUES(
	DEFAULT
	,#{categoryName}
	,#{desc}
	,#{parentCategoryId}
	,#{imageUrl}
)

mapper.select:
SELECT 
	ID
	,CATEGORY_NAME
	,`DESC`
	,PARENT_CATEGORY_ID
	,IMAGE_URL
FROM
	CATEGORY
WHERE 1 = 1

		AND CATEGORY_NAME = #{categoryName}


		AND `DESC` = #{desc}


		AND PARENT_CATEGORY_ID = #{parentCategoryId}

	LIMIT
	#{offset}
	,#{limit}

SELECT
	COUNT(1)
FROM
	CATEGORY
WHERE 1 = 1

		AND CATEGORY_NAME = #{categoryName}


		AND `DESC` = #{desc}


		AND PARENT_CATEGORY_ID = #{parentCategoryId}

mapper.update:
UPDATE 
	CATEGORY
SET
	CATEGORY_NAME = #{categoryName}
	,`DESC` = #{desc}
	,PARENT_CATEGORY_ID = #{parentCategoryId}
WHERE

	ID = #{id}
