mapper.select:
SELECT ID ,USER_ID ,TITLE ,MAIN_CAREER ,SERVICE_TIME ,PARENT_CATEGORY ,SUB_CATEGORY ,STATE ,IMAGE_URL ,CREATE_TIME ,UPDATE_TIME FROM HUMANBOOK WHERE 1 = 1
AND PARENT_CATEGORY = #{categoryId}
AND SUB_CATEGORY = #{subCategoryId}
LIMIT #{offset} ,#{limit}

SELECT COUNT(*) FROM HUMANBOOK WHERE 1 = 1
AND PARENT_CATEGORY = #{categoryId}
AND SUB_CATEGORY = #{subCategoryId}

